import * as R from 'ramda'

export const getPhoneById = (state, id) => R.prop(id, state.phones)
// export const getPhoneById = (state, id) => state.phones[id] // равнозначный код

export const getPhones = (state) => {
  const applySearh = item => R.contains(
    state.phonesPage.search,
    R.prop('name', item)
    // item.name
  )
  const phones = R.compose(
    R.filter(applySearh),
    R.map(id => getPhoneById(state, id))
  )(state.phonesPage.ids)
  // const phones = R.map(id => getPhoneById(state, id), state.phonesPage.ids)
  return phones
}

export const getRenderedPhonesLength = state => R.length(state.phones.ids)

export const getTotalBasketCount = state => R.length(state.basket)
// export const getTotalBasketCount = (state) => state.basket.length

export const getTotalBasketPrice = state => {
  const totalPrice = R.compose(
    R.sum,
    R.pluck('price'),
    R.map(id => getPhoneById(state, id))
  )(state.basket)

  return totalPrice
}
